jQuery(document).ready(function ($) {

	$(".slider-link").click(function() {
	    var location = window.location.href;
    	var cur_url = '/' + location.split('/').pop();
    	var link = $(this).find('a').attr('href');
 
        if (cur_url == link)
        {
            $(this).addClass('active');
        }
	});

});